import React, { useState } from "react";
import FlowerNav from "../FlowerNav";
import FlowerField from "../FlowerField";
import { FlowerIntro } from "../FlowerIntro";
import { FlowerChoosingBouquet } from "../FlowerChoosingBouquet";

export const FlowersRenderComponent = () => {
  const [numberOfItems, setNumberOfItems] = useState(0);

  const cartValue = (value) => {
    setNumberOfItems(value);
  };

  return (
    <>
      <FlowerNav cost={numberOfItems} />
      <FlowerField />
      <FlowerIntro />
      <FlowerChoosingBouquet cartValue={cartValue.cartValue} />
    </>
  );
};
