import React, { useId, useReducer, useRef } from "react";
import {
  DELIVERY_STATE,
  STATEPROPS,
  delivery_reduser,
} from "./reduser/formReduser";

export const FlowerCartAddress = () => {
  const addressID = useId();
  const radioID = useId();
  const textReferanse = useRef();
  const radioReferanse = useRef();

  const [inState, dispatch] = useReducer(delivery_reduser, DELIVERY_STATE);

  const handleChange = (e) => {
    dispatch({
      type: STATEPROPS.TEXT_CHANGING,
      devinformation: { name: e.target.name, value: e.target.value },
    });
  };

  const handleRadioChange = (e) => {
    dispatch({ type: STATEPROPS.RADIO_CHANGED, radioChange: e.target.value });
  };

  // console.log(inState);

  return (
    <article className="Flowers__Cart__DeliveryInfo">
      <form
        id="Flowers__Cart__DeliveryInfo__DeliveryAttributes"
        onChange={handleChange}
      >
        <label htmlFor={`${addressID}_city`}>City or suburban:</label>
        <input
          type="address"
          name="city"
          defaultValue={inState.city}
          id={`${addressID}_city`}
        />
        <label htmlFor={`${addressID}_address`}>Address to deliver:</label>
        <input
          type="address"
          name="address"
          defaultValue={inState.address}
          id={`${addressID}_address`}
        />
        <label htmlFor={`${addressID}_date`}>Delivery data:</label>
        <input
          type="date"
          name="deliveryData"
          defaultValue={inState.deliveryData}
          id={`${addressID}_date`}
        />
        <label htmlFor={`${addressID}_time`}>Better to deliver on:</label>
        <select
          id={`${addressID}_time`}
          defaultValue={inState.deliveryTime}
          name="deliveryTime"
        >
          <option>12-14</option>
          <option>14-16</option>
          <option>16-18</option>
          <option>18-20</option>
          <option>20-22</option>
        </select>
        <label htmlFor={`${addressID}_comment`}>Additional comment:</label>
        <textarea
          ref={textReferanse}
          name="comment"
          defaultValue={inState.comment}
          id={`${addressID}_comment`}
        ></textarea>
      </form>
      <form
        id="Flowers__Cart__DeliveryInfo__DeliveryTypeRadio"
        defaultValue={inState.typeOfOrder}
        onChange={handleRadioChange}
        ref={radioReferanse}
      >
        <label>
          <input
            type="radio"
            name="typeOfOrder"
            defaultValue="Delivery"
            id={`${radioID}001`}
          />
          Delivery
        </label>
        <label>
          <input
            type="radio"
            name="typeOfOrder"
            defaultValue="Take away"
            id={`${radioID}002`}
          />
          Take away
        </label>
      </form>
      <label htmlFor={`${addressID}_promocode`}>Promocode (optional):</label>
      <input
        type="text"
        onChange={handleChange}
        name="promocode"
        id={`${addressID}_promocode`}
      />
    </article>
  );
};
