import React, { useState } from "react";

export const FlowerOptions = (props) => {
  let flowers = [
    {
      bouquet_0: {
        img_src:
          "https://www.kwiatowadostawa.pl/userdata/gfx/6673ef06705eda8ca3aa005170a4d600.jpg",
        name: "Bouquet #1 Roses",
        prise: {
          option_0: 14.99,
          option_1: 18.99,
          option_2: 23.99,
        },
        options: {
          option_0: "7 flowers",
          option_1: "11 flowers",
          option_2: "15 flowers",
        },
      },
    },
    {
      bouquet_1: {
        img_src:
          "https://www.kwiatowadostawa.pl/userdata/gfx/009a63754545543f82efabfdddc6a716.jpg",
        name: "Bouquet #2 Roses",
        prise: {
          option_0: 14.99,
          option_1: 18.99,
          option_2: 23.99,
        },
        options: {
          option_0: "8 flowers",
          option_1: "12 flowers",
          option_2: "16 flowers",
        },
      },
    },
    {
      bouquet_2: {
        img_src:
          "https://i.etsystatic.com/6075796/r/il/d216b1/4174042036/il_fullxfull.4174042036_rtc1.jpg",
        name: "Bouquet for weddings",
        prise: {
          option_0: 12.99,
          option_1: 14.99,
          option_2: 16.99,
        },
        options: {
          option_0: "12 flower",
          option_1: "14 flower",
          option_2: "16 flower",
        },
      },
    },
    {
      bouquet_3: {
        img_src:
          "https://vilniusflowers.lt/wp-content/uploads/2019/09/100-red-roses-bouquet.jpg",
        name: "Roses",
        prise: {
          option_0: 10.99,
          option_1: 16.99,
          option_2: 22.99,
          option_3: 28.99,
          option_4: 34.99,
        },
        options: {
          option_0: "5 flower",
          option_1: "9 flower",
          option_2: "13 flower",
          option_3: "17 flower",
          option_4: "21 flower",
        },
      },
    },
  ];

  const [work, setWork] = useState({
    ...flowers.map((el, index) => {
      return el[`bouquet_${index}`].prise.option_0;
    }),
  });
  const [cartCost, setCartCost] = useState(0);
  const [cartItem, setCartItem] = useState(0);

  const onFormCostchange = (e) => {
    e.preventDefault();

    const selectElements = [...document.querySelectorAll("select")];
    const selectedEl = selectElements.indexOf(e.target);
    const selectedElIndex = selectElements[selectedEl].selectedIndex;

    let selectedCostChanged = (work[selectedEl] =
      flowers[selectedEl][`bouquet_${selectedEl}`]["prise"][
        `option_${selectedElIndex}`
      ]);
    setWork({ ...work, selectedCostChanged });
  };

  const onClickAddtocart = (e) => {
    const selectElements = [
      ...document.querySelectorAll(".buttonHolder__toCart"),
    ];
    const selectedEl = selectElements.indexOf(e.target);
    const workCostSrt = work[selectedEl];

    setCartCost((prevCartCost) => prevCartCost + workCostSrt);
    setCartItem((prevCartItem) => prevCartItem + 1);
    props.cartItems(cartItem);
    props.cartCostTotal(cartCost);
  };

  return flowers.map((element, index) => {
    let bouquetShort = `bouquet_${index}`;
    let bouquetOption = Object.entries(element[bouquetShort].options);

    return (
      <div className="ChoosingFlowers__container__section" key={index}>
        <img
          src={element[bouquetShort].img_src}
          alt={element[bouquetShort].name}
        />
        <div>
          <section className="ChoosingFlowers__container__section__part--top">
            <span>{element[bouquetShort].name}</span>
            <span>{work[index] + "$"}</span>
          </section>
          <section className="ChoosingFlowers__container__section__part--bottom">
            <form onChange={onFormCostchange}>
              <select
                name="NumberOfflowers"
                className="ChoosingFlowers__container__section__select"
              >
                {bouquetOption.map((el, number) => {
                  let bouquet = el[1];

                  return (
                    <option key={number} value={bouquet}>
                      {bouquet}
                    </option>
                  );
                })}
              </select>
            </form>
            <div className="buttonHolder">
              <button
                className="buttonHolder__toCart"
                onClick={onClickAddtocart}
              >
                To cash
              </button>
              <button>Buy now</button>
            </div>
          </section>
        </div>
      </div>
    );
  });
};
