import React from "react";

const flovers = [
  {
    bouquet_0: {
      img_src:
        "https://www.kwiatowadostawa.pl/userdata/gfx/6673ef06705eda8ca3aa005170a4d600.jpg",
      name: "bouquet #1 Roses",
      cost: {
        option_0: "14,99$",
        option_1: "18,99$",
        option_2: "23,99$",
      },
      options: {
        option_0: "7 flovers",
        option_1: "11 flovers",
        option_2: "15 flovers",
      },
    },
  },
  {
    bouquet_1: {
      img_src:
        "https://www.kwiatowadostawa.pl/userdata/gfx/009a63754545543f82efabfdddc6a716.jpg",
      name: "bouquet #2 Roses",
      cost: {
        option_0: "14,99$",
        option_1: "18,99$",
        option_2: "23,99$",
      },
      options: {
        option_0: "7 flovers",
        option_1: "11 flovers",
        option_2: "15 flovers",
      },
    },
  },
];

export const FloverOptions = () => {
  return flovers.map((el, number) => {
    let bouquet = `bouquet_${number}`;

    return (
      <div>
        <img src={bouquet.img_src} alt={bouquet.name} />
        <span>{bouquet.name}</span>
        <select
          name="Number of flovers"
          class="ChoosingFlowers__container__select"
        >
          {flovers.map((el, number) => {
            let bouquet = `bouquet_${number}`;
            let option = `option_${number}`;

            return (
              <option value={bouquet.option[option]}>
                {bouquet.cost[option]}
              </option>
            );
          })}
        </select>
      </div>
    );
  });
};
