export const CART_RENDER_ACTION_TYPE = {
  ADDING_NEW_POS: "ADDING_NEW_POS",
  REMOVE_POSITION: "REMOVE_POSITION",
};

export const cartOrderRenderState = [];

export const cartOrderRenderReduser = (state, action) => {
  switch (action.type) {
    case CART_RENDER_ACTION_TYPE.ADDING_NEW_POS: {
      if (action.cart_Object.id !== state.id) {
        return [
          ...state,
          {
            id: action.cart_Object.id,
            img: action.cart_Object.img,
            name: action.cart_Object.name,
            type: { ...action.cart_Object.option },
            quantity: action.cart_Object.quantity,
            cost: action.cart_Object.cost,
            costTotal: action.cart_Object.cost * action.cart_Object.quantity,
          },
        ];
      } else {
        return [...state, state.quantity++];
      }
    }
    case CART_RENDER_ACTION_TYPE.REMOVE_POSITION: {
      return action.id;
    }
    default:
      return state;
  }
};
