import React, { useContext } from "react";
import flowers from "../../flowerslist/flowersList";
import { CART_RENDER_ACTION_TYPE } from "../../cartOrderRender/cartOrderRender";
import { ObjectContext } from "../../../context/ObjectContext";

export const FlowerCartSummary = () => {
  const { cartRender, dispatchRender } = useContext(ObjectContext);

  return cartRender.map((item) => {
    const quantityShortcut = item.quantity;
    const itemSearchShortcut = flowers.find((it) => it.id === item.id);
    const numberOfFlowersShortcut = Object.keys(quantityShortcut || {});
    const onToggleRemoveButton = () => {
      dispatchRender({
        type: CART_RENDER_ACTION_TYPE.REMOVE_POSITION,
        id: item.id,
      });
    };

    return (
      <section key={item.id} className="Flowers__Cart__PositionContainer">
        <img src={itemSearchShortcut?.img_src} alt="" />
        <article>
          <span>{itemSearchShortcut?.name}</span>
          <span>
            {numberOfFlowersShortcut == null ? (
              <>G</>
            ) : (
              numberOfFlowersShortcut.map((element) => {
                if (quantityShortcut[element] === 0) {
                  return <></>;
                } else {
                  return (
                    <div
                      key={element}
                      className="Flowers__Cart__PositionContainer--Info"
                    >
                      <span>
                        {itemSearchShortcut?.options[`option_${element}`]}
                        {" - "}
                        {itemSearchShortcut?.prise[`option_${element}`]}$
                      </span>
                      <span>x{quantityShortcut[element]}</span>
                    </div>
                  );
                }
              })
            )}
          </span>
        </article>
        <div
          className="Flowers__Cart__PositionContainer--CloseButton"
          onClick={onToggleRemoveButton}
        ></div>
      </section>
    );
  });
};
