import React, { useState } from "react";
import FlowerNav from "../FlowerNav";
import FlowerField from "../FlowerField";
import { FlowerIntro } from "../FlowerIntro";
import { FlowerChoosingBouquet } from "../FlowerChoosingBouquet";
import { CartItemsContext } from "../../context/CartContext";

export const FlowersRenderComponent = () => {
  let [value, setValue] = useState(0);
  let valueLocalStorage = localStorage.get(value);

  return (
    <CartItemsContext.Provider value={{ value, setValue }}>
      <FlowerNav />
      <FlowerField />
      <FlowerIntro />
      <FlowerChoosingBouquet />
    </CartItemsContext.Provider>
  );
};
