import React, { useState, useReducer } from "react";
import FlowerNav from "./FlowerNav";
import { FlowerMainPage } from "./FlowerMainPage";
import { CartItemsContext } from "../../context/CartContext";
import { FlowerFooter } from "./FlowerFooter";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import { FlowerCart } from "./cart/FlowerCart";
import { FlowerAbout } from "./about/FlowerAbout";
import { FlowerContact } from "./contact/FlowerContact";
import useLocalStorage from "../../customHooks/useLocalStorage";
import { ObjectContext } from "../../context/ObjectContext";
import {
  cartOrderRenderReduser,
  cartOrderRenderState,
} from "../cartOrderRender/cartOrderRender";

export const FlowersRenderComponent = () => {
  let [cartValue, setCartValue] = useLocalStorage("cartValue", {
    cartItem: 0,
    cartCost: 0,
  });
  const [cartState, dispatchData] = useReducer(
    cartOrderRenderReduser,
    cartOrderRenderState
  );

  return (
    <BrowserRouter>
      <CartItemsContext.Provider
        value={{ cartValue: cartValue, setCartValue: setCartValue }}
      >
        <ObjectContext.Provider
          value={{
            cartOrderRenderState: cartOrderRenderState,
            cartOrderRenderReduser: cartOrderRenderReduser,
          }}
        >
          <FlowerNav />
          <main>
            <Routes>
              <Route index element={<FlowerMainPage />} />
              <Route path="flowerCart" element={<FlowerCart />} />
              <Route path="flowerAbout" element={<FlowerAbout />} />
              <Route path="flowerContact" element={<FlowerContact />} />
            </Routes>
          </main>
          <FlowerFooter />
        </ObjectContext.Provider>
      </CartItemsContext.Provider>
    </BrowserRouter>
  );
};
