import React, { useContext } from "react";
import { CartItemsContext } from "../../../context/CartContext";
import { FlowerCartAddress } from "./FlowerCartAddress";

export const FlowerCart = () => {
  const { cartValue } = useContext(CartItemsContext);

  return (
    <section className="Flowers__Cart">
      <div>Your order: Summary</div>
      <FlowerCartAddress />
      <div>
        <article>
          {cartValue.cartItem === 0
            ? `You ordered nothing`
            : `Total items: ${cartValue.cartItem}`}
        </article>
        <article>Total cost: {cartValue.cartCost}$</article>
        <button>Confirm order</button>
      </div>
    </section>
  );
};
