import React from "react";

export const FloverOptions = () => {
  const flovers = [
    {
      bouquet_0: {
        img_src:
          "https://www.kwiatowadostawa.pl/userdata/gfx/6673ef06705eda8ca3aa005170a4d600.jpg",
        name: "bouquet #1 Roses",
        cost: {
          option_0: "14,99$",
          option_1: "18,99$",
          option_2: "23,99$",
        },
        options: {
          option_0: "7 flovers",
          option_1: "11 flovers",
          option_2: "15 flovers",
        },
      },
    },
    {
      bouquet_1: {
        img_src:
          "https://www.kwiatowadostawa.pl/userdata/gfx/009a63754545543f82efabfdddc6a716.jpg",
        name: "bouquet #2 Roses",
        cost: {
          option_0: "14,99$",
          option_1: "18,99$",
          option_2: "23,99$",
        },
        options: {
          option_0: "7 flovers",
          option_1: "11 flovers",
          option_2: "15 flovers",
        },
      },
    },
    {
      bouquet_2: {
        img_src:
          "https://t4.ftcdn.net/jpg/02/12/03/51/360_F_212035168_ZcKb81yNi4tL480i05XiT26tEN95ynJO.jpg",
        name: "Shit",
        cost: {
          option_0: "14,99$",
          option_1: "18,99$",
          option_2: "23,99$",
        },
        options: {
          option_0: "12",
          option_1: "14",
          option_2: "16",
        },
      },
    },
  ];

  return flovers.map((element, index) => {
    let bouquetShort = `bouquet_${index}`;
    let bouquetOption = Object.entries(element[bouquetShort].options);
    // Object.entries(bouquetOption);
    // console.log(bouquetOption);

    return (
      <div key={index}>
        <img
          src={element[bouquetShort].img_src}
          alt={element[bouquetShort].name}
        />
        <span>{element[bouquetShort].name}</span>
        <select
          name="NumberOfFlovers"
          className="ChoosingFlowers__container__select"
        >
          <option>Choose option</option>
          {bouquetOption.map((el, number) => {
            let bouquet = el[1];

            return (
              <option key={number} value={bouquet}>
                {bouquet}
              </option>
            );
          })}
        </select>
      </div>
    );
  });
};
